// App.js
import React, { useState, useEffect, useRef } from "react";
import "./App.css";

function Drum({ drum, toggleNote, updateDrumPosition }) {
  const [dragging, setDragging] = useState(false);
  const [startMouse, setStartMouse] = useState({ x: 0, y: 0 });
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });
  const containerRef = useRef(null);

  const handleMouseDown = (e) => {
    // Only respond to left click
    if (e.button !== 0) return;
    setDragging(true);
    // Store the mouse position and the drum‚Äôs current (x,y)
    setStartMouse({ x: e.clientX, y: e.clientY });
    setStartPos({ x: drum.x, y: drum.y });
    e.preventDefault();
  };

  const handleMouseMove = (e) => {
    if (!dragging) return;
    // Calculate how far the mouse has moved since the initial click
    const dx = e.clientX - startMouse.x;
    const dy = e.clientY - startMouse.y;
    // Update the drum‚Äôs position continuously
    updateDrumPosition(drum.id, startPos.x + dx, startPos.y + dy, null, false);
  };

  const handleMouseUp = (e) => {
    if (!dragging) return;
    setDragging(false);
    const dx = e.clientX - startMouse.x;
    const dy = e.clientY - startMouse.y;

    // Get final bounding rect for collision check if needed
    const rect = containerRef.current?.getBoundingClientRect() || null;
    // Finalize position
    updateDrumPosition(drum.id, startPos.x + dx, startPos.y + dy, rect, true);
  };

  // Listen on document so dragging continues even if mouse leaves the drum area
  useEffect(() => {
    if (dragging) {
      document.addEventListener("mousemove", handleMouseMove);
      document.addEventListener("mouseup", handleMouseUp);
    } else {
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
    }
    return () => {
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
    };
  }, [dragging, startMouse, startPos]);

  return (
    <div
      ref={containerRef}
      className="drum-container"
      style={{
        position: "absolute",
        left: drum.x,
        top: drum.y,
        width: "60px",
        height: "60px",
        cursor: dragging ? "grabbing" : "grab",
      }}
      onMouseDown={handleMouseDown}
      onClick={() => {
        // Avoid toggling note if the user just dragged
        if (!dragging) toggleNote(drum);
      }}
    >
      <div className="drum-button">
        <div className="drum-circle">{drum.note}</div>
      </div>
    </div>
  );
}

function Tunepani() {
  // We create 4 default drums in an arrangement approximating your mockup:
  // E3, G4, B4, E4. Adjust x,y as you like.
  const [drums, setDrums] = useState([
    { id: 1, note: "E3", x: 100, y: 100 },
    { id: 2, note: "G4", x: 200, y: 80 },
    { id: 3, note: "B4", x: 140, y: 160 },
    { id: 4, note: "E4", x: 260, y: 130 },
  ]);

  const [pitch, setPitch] = useState(0);
  const [closestNote, setClosestNote] = useState("");
  const [volume, setVolume] = useState(0.3);
  const [menuOpen, setMenuOpen] = useState(false);
  const [savedConfigs, setSavedConfigs] = useState([]);
  const [configName, setConfigName] = useState("");

  const audioContextRef = useRef(null);
  const oscillatorsRef = useRef({});
  const analyserRef = useRef(null);
  const microphoneRef = useRef(null);
  const trashBinRef = useRef(null);

  // For simplicity, define some notes/frequencies. (You can expand or adjust.)
  const notes = ["E3", "G4", "B4", "E4"];
  const frequencies = {
    E3: 164.81,
    G4: 392.0,
    B4: 493.88,
    E4: 329.63,
  };

  useEffect(() => {
    // Microphone pitch detection, if desired
    navigator.mediaDevices
      .getUserMedia({ audio: true })
      .then((stream) => {
        if (!audioContextRef.current) {
          audioContextRef.current =
            new (window.AudioContext || window.webkitAudioContext)();
        }
        const audioContext = audioContextRef.current;
        analyserRef.current = audioContext.createAnalyser();
        analyserRef.current.fftSize = 2048;
        microphoneRef.current = audioContext.createMediaStreamSource(stream);
        microphoneRef.current.connect(analyserRef.current);
        detectPitch();
      })
      .catch((error) => console.error("Microphone access error:", error));

    // Load saved configs from localStorage
    const configs = JSON.parse(localStorage.getItem("tunepaniConfigs")) || [];
    setSavedConfigs(configs);
  }, []);

  const detectPitch = () => {
    const analyser = analyserRef.current;
    if (!analyser) return;
    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);
    const processPitch = () => {
      analyser.getByteFrequencyData(dataArray);
      const maxVal = Math.max(...dataArray);
      const maxIndex = dataArray.indexOf(maxVal);
      const frequency =
        maxIndex * (audioContextRef.current.sampleRate / analyser.fftSize);
      setPitch(frequency);
      setClosestNote(findClosestNote(frequency));
      requestAnimationFrame(processPitch);
    };
    processPitch();
  };

  const findClosestNote = (frequency) => {
    if (!frequency || frequency <= 0) return "";
    return Object.keys(frequencies).reduce((closest, note) =>
      Math.abs(frequencies[note] - frequency) <
      Math.abs(frequencies[closest] - frequency)
        ? note
        : closest
    );
  };

  const toggleNote = (drum) => {
    if (!frequencies[drum.note]) return;
    const audioContext = audioContextRef.current;
    if (audioContext?.state === "suspended") {
      audioContext.resume();
    }
    if (oscillatorsRef.current[drum.id]) {
      // Stop playing
      try {
        oscillatorsRef.current[drum.id].oscillator.stop(audioContext.currentTime);
      } catch (e) {
        console.error("Error stopping oscillator", e);
      }
      delete oscillatorsRef.current[drum.id];
    } else {
      // Start playing
      const freq = frequencies[drum.note];
      if (!isFinite(freq)) return;
      const oscillator = audioContext.createOscillator();
      const gainNode = audioContext.createGain();
      oscillator.type = "sine";
      oscillator.frequency.setValueAtTime(freq, audioContext.currentTime);
      gainNode.gain.setValueAtTime(volume, audioContext.currentTime);
      oscillator.connect(gainNode).connect(audioContext.destination);
      oscillator.start();
      oscillatorsRef.current[drum.id] = { oscillator, gainNode };
    }
  };

  // If you want to change a drum‚Äôs note from a menu, you can do it here:
  const handleNoteChange = (drum, newNote) => {
    setDrums((prev) =>
      prev.map((d) => (d.id === drum.id ? { ...d, note: newNote } : d))
    );
    if (oscillatorsRef.current[drum.id]) {
      const freq = frequencies[newNote];
      if (isFinite(freq)) {
        oscillatorsRef.current[drum.id].oscillator.frequency.setValueAtTime(
          freq,
          audioContextRef.current.currentTime
        );
      }
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    Object.values(oscillatorsRef.current).forEach(({ gainNode }) =>
      gainNode.gain.setValueAtTime(newVolume, audioContextRef.current.currentTime)
    );
  };

  // For demonstration, we skip fancy "Add Drum" code here and just have 4 by default.
  // But you could reintroduce your addDrum logic if you like.

  const saveConfiguration = () => {
    const name = configName.trim() || "Untitled Piece";
    const newConfig = { id: Date.now(), name, drums };
    const updatedConfigs = [...savedConfigs, newConfig];
    setSavedConfigs(updatedConfigs);
    localStorage.setItem("tunepaniConfigs", JSON.stringify(updatedConfigs));
    setConfigName("");
  };

  const loadConfiguration = (config) => {
    setDrums(config.drums);
    setMenuOpen(false);
  };

  const deleteConfiguration = (configId) => {
    const updatedConfigs = savedConfigs.filter((c) => c.id !== configId);
    setSavedConfigs(updatedConfigs);
    localStorage.setItem("tunepaniConfigs", JSON.stringify(updatedConfigs));
  };

  const handleConfigNameChange = (e) => setConfigName(e.target.value);

  // Update a drum‚Äôs position on each mouse move.
  // If final=true, check collision with the trash bin to remove the drum if it‚Äôs dropped there.
  const updateDrumPosition = (drumId, x, y, rect, final) => {
    if (isNaN(x) || isNaN(y)) return; // guard against invalid
    if (final && trashBinRef.current && rect) {
      const trashRect = trashBinRef.current.getBoundingClientRect();
      // Simple overlap check
      if (
        rect.right > trashRect.left &&
        rect.left < trashRect.right &&
        rect.bottom > trashRect.top &&
        rect.top < trashRect.bottom
      ) {
        setDrums((prev) => prev.filter((d) => d.id !== drumId));
        if (oscillatorsRef.current[drumId]) {
          // Stop any ongoing oscillator
          try {
            oscillatorsRef.current[drumId].oscillator.stop(
              audioContextRef.current.currentTime
            );
          } catch (e) {
            console.error("Error stopping oscillator", e);
          }
          delete oscillatorsRef.current[drumId];
        }
        return;
      }
    }
    // Otherwise, just update position
    setDrums((prev) =>
      prev.map((d) => (d.id === drumId ? { ...d, x, y } : d))
    );
  };

  const closeMenu = () => setMenuOpen(false);

  // Simple pitch gauge calculation
  let markerLeft = 100;
  let gaugeNote = "";
  if (pitch > 0 && closestNote) {
    const ideal = frequencies[closestNote];
    if (ideal) {
      let cents = 1200 * Math.log2(pitch / ideal);
      cents = Math.max(-50, Math.min(50, cents));
      markerLeft = 100 + (cents / 50) * 100;
      gaugeNote = closestNote;
    }
  }

  return (
    <div className="container" style={{ backgroundColor: "#000", color: "#fff" }}>
      <header className="header" style={{ display: "flex", alignItems: "center" }}>
        <button
          className="menu-button"
          onClick={() => setMenuOpen(!menuOpen)}
          style={{ marginLeft: "8px" }}
        >
          ‚ò∞
        </button>
        <h1 className="title" style={{ flex: 1, textAlign: "center" }}>
          Tune Pani
        </h1>
        {/* We skip an "Add Drum" button for simplicity in this demo */}
      </header>

      {menuOpen && <div className="overlay" onClick={closeMenu}></div>}

      {menuOpen && (
        <aside
          className="sidebar"
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "200px",
            height: "100%",
            background: "#333",
            padding: "10px",
            boxSizing: "border-box",
          }}
        >
          <h3>Save Piece</h3>
          <input
            type="text"
            placeholder="Piece Name"
            value={configName}
            onChange={handleConfigNameChange}
            className="config-input"
            style={{ width: "100%" }}
          />
          <button className="config-button" onClick={saveConfiguration}>
            Save
          </button>
          <h3>Pieces</h3>
          <ul className="config-list" style={{ listStyle: "none", padding: 0 }}>
            {savedConfigs.map((config) => (
              <li key={config.id} style={{ marginBottom: "4px" }}>
                <button
                  className="load-button"
                  onClick={() => loadConfiguration(config)}
                  style={{ marginRight: "6px" }}
                >
                  {config.name}
                </button>
                <span
                  className="config-delete"
                  onClick={() => deleteConfiguration(config.id)}
                  style={{ cursor: "pointer", color: "red" }}
                >
                  x
                </span>
              </li>
            ))}
          </ul>
          <button className="close-sidebar" onClick={() => setMenuOpen(false)}>
            Close
          </button>
        </aside>
      )}

      {/* Drum area for free placement */}
      <div
        className="drum-area"
        style={{
          position: "relative",
          width: "100%",
          height: "400px",
          border: "1px solid #666",
          marginTop: "10px",
        }}
      >
        {drums.map((drum) => (
          <Drum
            key={drum.id}
            drum={drum}
            toggleNote={toggleNote}
            updateDrumPosition={updateDrumPosition}
          />
        ))}
      </div>

      {/* Trash bin fixed at the bottom */}
      <div
        className="trash-bin"
        ref={trashBinRef}
        style={{
          position: "fixed",
          bottom: "20px",
          left: "50%",
          transform: "translateX(-50%)",
          background: "#f8d7da",
          padding: "10px",
          borderRadius: "5px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          zIndex: 1000,
          color: "#000",
        }}
      >
        <span className="trash-icon">üóëÔ∏è</span>
      </div>

      <main className="main-content" style={{ marginTop: "20px" }}>
        <div className="gauge-container" style={{ display: "flex", alignItems: "center" }}>
          <span className="gauge-label" style={{ marginRight: "10px" }}>
            Flat
          </span>
          <div
            className="gauge"
            style={{
              position: "relative",
              width: "200px",
              height: "4px",
              background: "#aaa",
              marginRight: "10px",
            }}
          >
            <div
              className="gauge-marker"
              style={{
                position: "absolute",
                top: "-3px",
                width: "2px",
                height: "10px",
                background: "#ff0",
                left: markerLeft + "px",
              }}
            ></div>
          </div>
          <span className="gauge-label">Sharp</span>
        </div>
        <div className="gauge-note" style={{ marginTop: "10px" }}>
          Detected Note: {gaugeNote}
        </div>
        <div className="volume-control" style={{ marginTop: "10px" }}>
          <label htmlFor="volume-slider" style={{ marginRight: "8px" }}>
            Volume:
          </label>
          <input
            id="volume-slider"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={handleVolumeChange}
          />
        </div>
      </main>
    </div>
  );
}

export default Tunepani;
