import React, { useState, useEffect, useRef } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import "./App.css";

function Drum({ drum, index, toggleNote, handleNoteChange, notes, arcStyle }) {
  // Refs for touch/tap handling.
  const touchStartRef = useRef(0);
  const lastTapRef = useRef(0);
  const singleTapTimeoutRef = useRef(null);
  const longPressTimeoutRef = useRef(null);
  const touchMovedRef = useRef(false);

  const [showContextMenu, setShowContextMenu] = useState(false);
  const contextMenuRef = useRef(null);
  const containerRef = useRef(null);

  const LONG_PRESS_THRESHOLD = 600; // ms before drag initiates
  const DOUBLE_TAP_DELAY = 300; // ms for detecting double tap

  // Desktop double-click handler.
  const handleDoubleClick = (e) => {
    e.stopPropagation();
    setShowContextMenu(true);
  };

  // When a note is selected from the context menu.
  const selectNote = (note) => {
    handleNoteChange(drum, note);
    setShowContextMenu(false);
  };

  // Close context menu if clicking outside.
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        contextMenuRef.current &&
        !contextMenuRef.current.contains(event.target) &&
        containerRef.current &&
        !containerRef.current.contains(event.target)
      ) {
        setShowContextMenu(false);
      }
    };
    if (showContextMenu) {
      document.addEventListener("mousedown", handleClickOutside);
      document.addEventListener("touchstart", handleClickOutside);
    }
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
      document.removeEventListener("touchstart", handleClickOutside);
    };
  }, [showContextMenu]);

  return (
    <Draggable draggableId={String(drum.id)} index={index}>
      {(provided, snapshot) => {
        // Custom touch handlers for mobile.
        const onTouchStart = (e) => {
          touchStartRef.current = Date.now();
          touchMovedRef.current = false;
          longPressTimeoutRef.current = setTimeout(() => {
            if (
              provided.dragHandleProps &&
              provided.dragHandleProps.onTouchStart
            ) {
              provided.dragHandleProps.onTouchStart(e);
            }
          }, LONG_PRESS_THRESHOLD);
        };

        const onTouchMove = (e) => {
          touchMovedRef.current = true;
          if (longPressTimeoutRef.current) {
            clearTimeout(longPressTimeoutRef.current);
            longPressTimeoutRef.current = null;
          }
          if (
            provided.dragHandleProps &&
            provided.dragHandleProps.onTouchMove
          ) {
            provided.dragHandleProps.onTouchMove(e);
          }
        };

        const onTouchEnd = (e) => {
          if (longPressTimeoutRef.current) {
            clearTimeout(longPressTimeoutRef.current);
            longPressTimeoutRef.current = null;
            const now = Date.now();
            const duration = now - touchStartRef.current;
            if (duration < LONG_PRESS_THRESHOLD) {
              // Process as a tap. Check for double tap.
              if (now - lastTapRef.current < DOUBLE_TAP_DELAY) {
                if (singleTapTimeoutRef.current)
                  clearTimeout(singleTapTimeoutRef.current);
                lastTapRef.current = 0;
                setShowContextMenu(true);
              } else {
                lastTapRef.current = now;
                singleTapTimeoutRef.current = setTimeout(() => {
                  toggleNote(drum);
                }, DOUBLE_TAP_DELAY);
              }
            }
          }
          if (
            provided.dragHandleProps &&
            provided.dragHandleProps.onTouchEnd
          ) {
            provided.dragHandleProps.onTouchEnd(e);
          }
        };

        return (
          <div
            className="drum-container"
            ref={(ref) => {
              provided.innerRef(ref);
              containerRef.current = ref;
            }}
            // Spread all provided props so that desktop mouse events work.
            {...provided.draggableProps}
            {...provided.dragHandleProps}
            // Override touch events with our custom handlers.
            onTouchStart={onTouchStart}
            onTouchMove={onTouchMove}
            onTouchEnd={onTouchEnd}
            onClick={(e) => {
              if (!snapshot.isDragging) {
                toggleNote(drum);
              }
            }}
            onDoubleClick={handleDoubleClick}
            style={{ ...provided.draggableProps.style, ...arcStyle }}
          >
            {/* Using a div instead of a button to prevent native interference */}
            <div className="drum-button">
              <div className="drum-circle">{drum.note}</div>
            </div>
            {showContextMenu && (
              <div className="context-menu" ref={contextMenuRef}>
                {notes.map((note) => (
                  <div
                    key={note}
                    className="context-menu-item"
                    onClick={() => selectNote(note)}
                  >
                    {note}
                  </div>
                ))}
              </div>
            )}
          </div>
        );
      }}
    </Draggable>
  );
}

function Tunepani() {
  // Extended note range from C2 to C5.
  const timpaniNotes = [
    "C2", "C#2", "D2", "D#2", "E2", "F2", "F#2", "G2", "G#2", "A2", "A#2", "B2",
    "C3", "C#3", "D3", "D#3", "E3", "F3", "F#3", "G3", "G#3", "A3", "A#3", "B3",
    "C4", "C#4", "D4", "D#4", "E4", "F4", "F#4", "G4", "G#4", "A4", "A#4", "B4",
    "C5"
  ];
  const timpaniFrequencies = {
    "C2": 65.41, "C#2": 69.30, "D2": 73.42, "D#2": 77.78, "E2": 82.41, "F2": 87.31, "F#2": 92.50, "G2": 98.00,
    "G#2": 103.83, "A2": 110.00, "A#2": 116.54, "B2": 123.47,
    "C3": 130.81, "C#3": 138.59, "D3": 146.83, "D#3": 155.56, "E3": 164.81, "F3": 174.61, "F#3": 185.00, "G3": 196.00,
    "G#3": 207.65, "A3": 220.00, "A#3": 233.08, "B3": 246.94,
    "C4": 261.63, "C#4": 277.18, "D4": 293.66, "D#4": 311.13, "E4": 329.63, "F4": 349.23, "F#4": 369.99, "G4": 392.00,
    "G#4": 415.30, "A4": 440.00, "A#4": 466.16, "B4": 493.88,
    "C5": 523.25
  };

  const [drums, setDrums] = useState([]);
  const [pitch, setPitch] = useState(0);
  const [closestNote, setClosestNote] = useState("");
  const [volume, setVolume] = useState(0.3);
  const [menuOpen, setMenuOpen] = useState(false);
  const [savedConfigs, setSavedConfigs] = useState([]);
  const [configName, setConfigName] = useState("");
  const [isDragging, setIsDragging] = useState(false);

  const notes = timpaniNotes;
  const frequencies = timpaniFrequencies;

  const audioContextRef = useRef(null);
  const oscillatorsRef = useRef({});
  const analyserRef = useRef(null);
  const microphoneRef = useRef(null);

  useEffect(() => {
    navigator.mediaDevices
      .getUserMedia({ audio: true })
      .then((stream) => {
        if (!audioContextRef.current) {
          audioContextRef.current =
            new (window.AudioContext || window.webkitAudioContext)();
        }
        const audioContext = audioContextRef.current;
        analyserRef.current = audioContext.createAnalyser();
        analyserRef.current.fftSize = 2048;
        microphoneRef.current = audioContext.createMediaStreamSource(stream);
        microphoneRef.current.connect(analyserRef.current);
        detectPitch();
      })
      .catch((error) =>
        console.error("Microphone access error:", error)
      );

    const configs = JSON.parse(localStorage.getItem("tunepaniConfigs")) || [];
    setSavedConfigs(configs);
  }, []);

  const detectPitch = () => {
    const analyser = analyserRef.current;
    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);
    const processPitch = () => {
      analyser.getByteFrequencyData(dataArray);
      const maxVal = Math.max(...dataArray);
      const maxIndex = dataArray.indexOf(maxVal);
      const frequency =
        maxIndex * (audioContextRef.current.sampleRate / analyser.fftSize);
      setPitch(frequency);
      setClosestNote(findClosestNote(frequency));
      requestAnimationFrame(processPitch);
    };
    processPitch();
  };

  const findClosestNote = (frequency) => {
    return notes.reduce((closest, note) =>
      Math.abs(frequencies[note] - frequency) <
      Math.abs(frequencies[closest] - frequency)
        ? note
        : closest
    );
  };

  const addDrum = () => setDrums([...drums, { id: Date.now(), note: "A3" }]);

  const removeDrum = (drumId) => {
    setDrums(drums.filter((drum) => drum.id !== drumId));
    if (oscillatorsRef.current[drumId]) {
      try {
        oscillatorsRef.current[drumId].oscillator.stop(
          audioContextRef.current.currentTime
        );
      } catch (e) {
        console.error("Error stopping oscillator", e);
      }
      delete oscillatorsRef.current[drumId];
    }
  };

  const toggleNote = (drum) => {
    const audioContext = audioContextRef.current;
    if (audioContext.state === "suspended") {
      audioContext.resume();
    }
    if (oscillatorsRef.current[drum.id]) {
      try {
        oscillatorsRef.current[drum.id].oscillator.stop(
          audioContext.currentTime
        );
      } catch (e) {
        console.error("Error stopping oscillator", e);
      }
      delete oscillatorsRef.current[drum.id];
    } else {
      const freq = frequencies[drum.note];
      if (!isFinite(freq)) {
        console.error("Invalid frequency for note", drum.note);
        return;
      }
      const oscillator = audioContext.createOscillator();
      const gainNode = audioContext.createGain();
      oscillator.type = "sine";
      oscillator.frequency.setValueAtTime(freq, audioContext.currentTime);
      gainNode.gain.setValueAtTime(volume, audioContext.currentTime);
      oscillator.connect(gainNode).connect(audioContext.destination);
      oscillator.start();
      oscillatorsRef.current[drum.id] = { oscillator, gainNode };
    }
  };

  const handleNoteChange = (drum, newNote) => {
    setDrums(
      drums.map((d) => (d.id === drum.id ? { ...d, note: newNote } : d))
    );
    if (oscillatorsRef.current[drum.id]) {
      const freq = frequencies[newNote];
      if (!isFinite(freq)) {
        console.error("Invalid frequency for note", newNote);
        return;
      }
      oscillatorsRef.current[drum.id].oscillator.frequency.setValueAtTime(
        freq,
        audioContextRef.current.currentTime
      );
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    Object.values(oscillatorsRef.current).forEach(({ gainNode }) =>
      gainNode.gain.setValueAtTime(newVolume, audioContextRef.current.currentTime)
    );
  };

  const saveConfiguration = () => {
    const name = configName.trim() || "Untitled Piece";
    const newConfig = { id: Date.now(), name: name, drums };
    const updatedConfigs = [...savedConfigs, newConfig];
    setSavedConfigs(updatedConfigs);
    localStorage.setItem("tunepaniConfigs", JSON.stringify(updatedConfigs));
    setConfigName("");
  };

  const loadConfiguration = (config) => {
    setDrums(config.drums);
    setMenuOpen(false);
  };

  const deleteConfiguration = (configId) => {
    const updatedConfigs = savedConfigs.filter(
      (config) => config.id !== configId
    );
    setSavedConfigs(updatedConfigs);
    localStorage.setItem("tunepaniConfigs", JSON.stringify(updatedConfigs));
  };

  const handleConfigNameChange = (e) => setConfigName(e.target.value);

  const onDragStart = () => {
    setIsDragging(true);
  };

  const onDragEnd = (result) => {
    setIsDragging(false);
    if (!result.destination) return;
    if (result.destination.droppableId === "trashBin") {
      const drumId = parseInt(result.draggableId, 10);
      removeDrum(drumId);
    } else if (result.destination.droppableId === "drum-list") {
      const newDrums = Array.from(drums);
      const [removed] = newDrums.splice(result.source.index, 1);
      newDrums.splice(result.destination.index, 0, removed);
      setDrums(newDrums);
    }
  };

  // Compute the arc style for each drum.
  const computeArcStyle = (index, total) => {
    const containerWidth = Math.min(600, window.innerWidth - 40);
    const containerHeight = 300;
    const margin = 20;
    const usableWidth = containerWidth - 60; // 60px = drum width
    let x =
      total > 1 ? margin + (usableWidth / (total - 1)) * index : containerWidth / 2;
    const centerX = containerWidth / 2;
    const baseMinY = 30; // highest point (center drum)
    const baseMaxY = 140; // lowest point (outer drums)
    const offsetY = (containerHeight - baseMaxY) / 2; // center vertically
    const k = (baseMaxY - baseMinY) / Math.pow(centerX - margin, 2);
    let y = baseMinY + k * Math.pow(x - centerX, 2) + offsetY;
    return {
      position: "absolute",
      left: `${x - 30}px`,
      top: `${y - 30}px`,
    };
  };

  let markerLeft = 100;
  let gaugeNote = "";
  if (pitch > 0 && closestNote) {
    let ideal = frequencies[closestNote];
    let cents = 1200 * Math.log2(pitch / ideal);
    cents = Math.max(-50, Math.min(50, cents));
    markerLeft = 100 + (cents / 50) * 100;
    gaugeNote = closestNote;
  }

  const closeMenu = () => setMenuOpen(false);

  return (
    <div className="container">
      <header className="header">
        <button className="menu-button" onClick={() => setMenuOpen(!menuOpen)}>
          ‚ò∞
        </button>
        <h1 className="title">Tunepani</h1>
        <button className="add-drum-icon" onClick={addDrum}>
          +
        </button>
      </header>

      {menuOpen && <div className="overlay" onClick={closeMenu}></div>}

      {menuOpen && (
        <aside className="sidebar">
          <h3>Save Piece</h3>
          <input
            type="text"
            placeholder="Piece Name"
            value={configName}
            onChange={handleConfigNameChange}
            className="config-input"
          />
          <button className="config-button" onClick={saveConfiguration}>
            Save
          </button>
          <h3>Pieces</h3>
          <ul className="config-list">
            {savedConfigs.map((config) => (
              <li key={config.id} className="config-item">
                <button className="load-button" onClick={() => loadConfiguration(config)}>
                  {config.name}
                </button>
                <span className="config-delete" onClick={() => deleteConfiguration(config.id)}>
                  x
                </span>
              </li>
            ))}
          </ul>
          <button className="close-sidebar" onClick={() => setMenuOpen(false)}>
            Close
          </button>
        </aside>
      )}

      <DragDropContext onDragStart={onDragStart} onDragEnd={onDragEnd}>
        <div>
          <Droppable
            droppableId="drum-list"
            isDropDisabled={false}
            isCombineEnabled={false}
            ignoreContainerClipping={false}
          >
            {(provided) => (
              <div
                className="drum-list"
                {...provided.droppableProps}
                ref={provided.innerRef}
              >
                {drums.map((drum, index) => (
                  <Drum
                    key={drum.id}
                    drum={drum}
                    index={index}
                    toggleNote={toggleNote}
                    handleNoteChange={handleNoteChange}
                    notes={notes}
                    arcStyle={computeArcStyle(index, drums.length)}
                  />
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
          <Droppable
            droppableId="trashBin"
            isDropDisabled={false}
            isCombineEnabled={false}
            ignoreContainerClipping={false}
          >
            {(provided) => (
              <div
                className="trash-bin"
                {...provided.droppableProps}
                ref={provided.innerRef}
                style={{ display: isDragging ? "flex" : "none" }}
              >
                <span className="trash-icon">üóëÔ∏è</span>
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </div>
      </DragDropContext>

      <main className="main-content">
        <div className="gauge-container">
          <span className="gauge-label">Flat</span>
          <div className="gauge">
            <div className="gauge-marker" style={{ left: markerLeft + "px" }}></div>
          </div>
          <span className="gauge-label">Sharp</span>
        </div>
        <div className="gauge-note">Detected Note: {gaugeNote}</div>
        <div className="volume-control">
          <label htmlFor="volume-slider">Volume:</label>
          <input
            id="volume-slider"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={handleVolumeChange}
          />
        </div>
      </main>
    </div>
  );
}

export default Tunepani;
